document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll('.artru-noanchor a[href^="#"], a[class*="artru-noanchor"]').forEach(function(e){e.addEventListener("click",function(e){e.preventDefault()})});let e=document.querySelector("#top"),t=document.querySelector(".artru-scrollTop");t.addEventListener("click",function(){window.scrollTo({top:e.offsetTop})});let n=document.querySelector("#artru-language_switcher"),o=document.querySelector("#hide-box-lang"),r=document.querySelector("#current-lang");n.addEventListener("mouseover",function(){o.style.display="block"}),n.addEventListener("mouseout",function(){o.style.display="none"}),document.addEventListener("click",function(){o.style.display="none"}),n.addEventListener("click",function(e){e.stopPropagation()}),r.addEventListener("touchend",function(e){e.preventDefault(),o.style.display="block"===o.style.display?"none":"block"})});